const storageName2 = "user_data";

class UserDataService {
    userData = null;

    get() {
        if (this.userData)
            return this.userData;

        if (localStorage === undefined)
            return null;
        const userData = localStorage.getItem(storageName2);
        this.userData = userData
        return userData;
    }

    set(userData) {
        this.userData = userData;
        let key = "token";
        let cleanJsonRegex = new RegExp(`,.*${key}.*[, ]`, "g");
        console.log("user_data" + JSON.stringify(userData));
        let nameJsonStr = userData.replace(cleanJsonRegex, "");

        localStorage.setItem(storageName2, JSON.stringify(nameJsonStr));
    }

    remove() {
        this.userData = null;
        localStorage.removeItem(storageName2);
    }
}

const service = new UserDataService();
export default service;
