import React, { useRef, useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import Header from '../../components/Header';
import mapboxgl from "mapbox-gl";
import AccessTokenService from "../../services/AccessTokenService";
import UserDataService from "../../services/UserDataService";

mapboxgl.accessToken = 'pk.eyJ1IjoiaGljY3VwLXN0YWZmIiwiYSI6ImNsangxeHVkbzAyNmwzdHJ4cmJiYWZmMm4ifQ.yI4259jlMq2elDiT_uK1kg';


export default function Control () {
    const [data, setData] = useState({});
    const [lng, setLng] = useState(2.35);
    const [lat, setLat] = useState(48.85);
    const [zoom, setZoom] = useState(10);
    const map = useRef(null);
    const mapContainer = useRef(null);



    useEffect(() => {
        const token = AccessTokenService.get();
        const data = UserDataService.get();
        if (map.current) return; // initialize map only once
        map.current = new mapboxgl.Map({
        container: mapContainer.current,
        style: 'mapbox://styles/mapbox/streets-v12',
        center: [lng, lat],
        zoom: zoom
        });

        async function fetchGeoJSON() {
            try {
                url = "https://pro-hikup.westeurope.cloudapp.azure.com/api/trail/retrieve";
                const requestData = {
                    user: data.user,
                    id: data.id,
                    roles: data.roles
                  };
                const requestOptions = {
                    method: 'POST',
                    headers: {
                      'Content-Type': 'application/json',
                      'Authorization': 'Bearer ' + token,
                    },
                    body: JSON.stringify(requestData)
                  };
                const response = await fetch();
                const parsed = await response.json();
                setData(parsed);
             } catch (error) {
                 console.error('Error fetching GeoJSON:', error);
               }
             }
            fetchGeoJSON();


        map.current.on("load", function () {
            // Add an image to use as a custom marker
            map.current.loadImage(
              "https://docs.mapbox.com/mapbox-gl-js/assets/custom_marker.png",
              function (error, image) {
                if (error) throw error;
                map.current.addImage("custom-marker", image);
                // Add a GeoJSON source with multiple points
                map.addSource("points", {
                  type: "geojson",
                  data: {
                    type: "FeatureCollection",
                    features: geoJson.features,
                  },
                });
            })});

            map.current.addControl(new mapboxgl.NavigationControl(), "top-right");

            // Clean up on unmount
            return () => map.current.remove();
        });


    return (
        <Layout>
                <style jsx>{`
                    .map-container {
                        position: absolute;
                        width: 100%;
                        height: 100%;
                    }
                    `}</style>
                    <div ref={mapContainer} className="map-container" />
            <title>Hik'UP Admin</title>
        </Layout>
    );
}
