import React, { useState, useRef, useEffect } from 'react';
import Layout from '../../components/Layout';
import { Button, Form, Input, Radio, Card } from 'antd';


export default function ProfileUpdate () {
    return (
        <Layout>
        <Card style={{alignVertical:"25%",left: "25%", width: "50%", alignHorizontal: "25%"}}>
        <style jsx>{`
                    .username {
                        width: 25%;
                    }
                    .password {
                        width: 10%;
                    }
                    .card {
                        width: 25%;
                        top: 50%;
                    }
                    .button-submit {
                        right: 25%;
                    }
                    `}</style>
        <Form>
          <Form.Item label="Username">
            <Input className="username" placeholder="Username"/>
          </Form.Item>
          <Form.Item label="Password">
            <Input className="password" placeholder="Password"/>
          </Form.Item>
          <Form.Item style={{paddingRight: "7%", paddingLeft: "7%"}} label="Confirm">
            <Input style={{paddingRight: "7%", paddingLeft: "7%"}} className="password" placeholder="Confirm Password"/>
          </Form.Item>
          <Form.Item >
            <Button style={{left: "85%", top:"50%"}} className="button-submit" type="primary">Submit</Button>
          </Form.Item>
        </Form>
        </Card>
        </Layout>

        )

    };
