import React, { useRef, useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import API from '../../services/API';
import mapboxgl, { accessToken } from "mapbox-gl";
import AccessTokenService from "../../services/AccessTokenService";
import UserDataService from "../../services/UserDataService";
import 'mapbox-gl/dist/mapbox-gl.css';
mapboxgl.accessToken = 'pk.eyJ1IjoiaGljY3VwLXN0YWZmIiwiYSI6ImNsangxeHVkbzAyNmwzdHJ4cmJiYWZmMm4ifQ.yI4259jlMq2elDiT_uK1kg';



export default function Control (access_token, user_data) {

    const [lng, setLng] = useState(2.35);
    const [lat, setLat] = useState(48.85);
    const [zoom, setZoom] = useState(7);
    const map = useRef(null);
    const mapContainer = useRef(null);



    useEffect(() => {
        const user_data = UserDataService.get();
        if (map.current) return; // initialize map only once
        map.current = new mapboxgl.Map({
        container: mapContainer.current,
        style: 'mapbox://styles/mapbox/streets-v12',
        center: [lng, lat],
        zoom: zoom
        });
        async function loadMarker() {
          const markers = await fetchGeoJSON(user_data);
          return geojson = {
            type: 'Feature',
            features: markers.map((marker) => ({
              geometry: {
                type: 'Point',
                coordinates: {
                  lat: marker[0],
                  lng: marker[1]
                }
              }
            }))
          };
        }
       let geojson =  loadMarker();
          map.current.on('load', () => {
            geojson.features.forEach((marker) => {  // create a DOM element for the marker
            const markerIcon = document.createElement('div');
            markerIcon.className = 'location-marker';
            markerIcon.style.backgroundImage = 'url(/location-marker.png)';
            markerIcon.style.width = marker.properties.iconSize[0] + 'px';
            markerIcon.style.height = marker.properties.iconSize[1] + 'px';

            new mapboxgl.Marker(markerIcon)
              .setLngLat(marker.geometry.coordinates)
              .addTo(map.current);
            });
          });
          map.current.addControl(new mapboxgl.NavigationControl(), "top-right");
          return () => { map.current.remove()};
      });

    return (
        <Layout>
                <style jsx>{`
                    .map-container {
                        position: absolute;
                        width: 100%;
                        height: 100%;
                    }
                    `}</style>
                    <div ref={mapContainer} className="map-container"/>
            <title>Hik'UP Admin</title>
        </Layout>
    );
}
